import SwiftUI
import AVFoundation
struct ContentView: View {
    @State var looper: AVPlayerLooper?
    init(){
        UITabBar.appearance().backgroundColor = UIColor(Color("under_color_bg"))
        UITabBar.appearance().unselectedItemTintColor = UIColor(Color("under_color_text_un"))
    }
    var body: some View {
                    
        //        let formatter = DateFormatter()
        //        formatter.dateFormat = "d"
        //        return Text(Date(), formatter: formatter)
        let formatter_w = DateFormatter()
        formatter_w.dateFormat = "EEEE"
        week = formatter_w.string(from: Date.now)
        
        if(week=="Monday"){
            week="週ㄧ"
        }else if(week=="Tuesday"){
            week="週二"
        }else if(week=="Wednesday"){
            week="週三"
        }else if(week=="Thursday"){
            week="週四"
        }else if(week=="Friday"){
            week="週五"
        }else if(week=="Saturday"){
            week="週六"
        }else if(week=="Sunday"){
            week="週日"
        }
        let formatter_y = DateFormatter()
        formatter_y.dateFormat = "y"
        year = formatter_y.string(from: Date.now)
        
        let formatter = DateFormatter()
        formatter.dateFormat = "d"
        day = formatter.string(from: Date.now)
        
        let formatter_m = DateFormatter()
        formatter_m.dateFormat = "M"
        month = formatter_m.string(from: Date.now)
        
        if(month=="1" || month=="2" || month=="3"){
            season="春"
        }else if(month=="4" || month=="5" || month=="6"){
            season="夏"
        }else if(month=="7" || month=="8" || month=="9"){
            season="秋"
        }else{
            season="冬"
        }
        
        if(month_int==1){//31
            Days=0+day_int
        }else if(month_int==2){//28
            Days=31+day_int
        }else if(month_int==3){//31
            Days=59+day_int
        }else if(month_int==4){//30
            Days=90+day_int
        }else if(month_int==5){//31
            Days=120+day_int
        }else if(month_int==6){//30
            Days=151+day_int
        }else if(month_int==7){//31
            Days=181+day_int
        }else if(month_int==8){//31
            Days=212+day_int
        }else if(month_int==9){//30
            Days=243+day_int
        }else if(month_int==10){//31
            Days=273+day_int
        }else if(month_int==11){//30
            Days=304+day_int
        }else{//31
            Days=334+day_int
        }
        
        if(month=="1"){
            story = story_1
        }else if(month=="2"){
            story = story_2
        }else if(month=="3"){
            story = story_3
        }else if(month=="4"){
            story = story_4
        }else if(month=="5"){
            story = story_5
        }else if(month=="6"){
            story = story_6
        }else if(month=="7"){
            story = story_7
        }else if(month=="8"){
            story = story_8
        }else if(month=="9"){
            story = story_9
        }else if(month=="10"){
            story = story_10
        }else if(month=="11"){
            story = story_11
        }else{
            story = story_12
        }
        
        return VStack() {
            
            ZStack{
                TabView {
                    NavigationView{
                        VStack(alignment: .leading, spacing: 10){
                            HStack{
                                Text("\(month) /")
                                    .font(.system(size: 35))
                                    .foregroundColor(.gray)
                                    .fontWeight(.light)
                                    .offset(x: 0, y: -50)
                                Text(day)
                                    .font(.system(size: 35))
                                    .foregroundColor(.gray)
                                    .fontWeight(.light)
                                    .offset(x: 0, y: -50)
                                VStack(alignment: .leading){
                                    
                                    Text(week)
                                        .font(.system(size: 14))
                                        .foregroundColor(.gray)
                                        .offset(x: 0, y: -50)
                                    Text(year)
                                        .font(.system(size: 14))
                                        .foregroundColor(.gray)
                                        .offset(x: 0, y: -50)
                                }
                            }
                            ZStack(alignment: .bottomTrailing){
                                Image("bg_white")
                                    .frame(width: 300, height: 500)
                                    .cornerRadius(10)
                                    .offset(x: 0, y: -50)
                                Text(season)
                                    .fontWeight(.light)
                                    .offset(x: -30, y: -450)
                                    .font(.system(size: 40))
                                
                                Text("\(month)月\(day_int)日是一年中的第\(Days)天（閏年是\(Days+1)天）。離一年的結束還有\(365-Days)天。")
                                    .fontWeight(.light)
                                    .frame(width: 250)
                                    .offset(x: -25, y: -250)
                                NavigationLink {
                                    RightPersonView()
                                } label: {
                                    Text("閱讀")
                                        .fontWeight(.light)
                                        .foregroundColor(Color("Color_button_text"))
                                        .frame(width: 60,height: 25)
                                        .background(Color(red: 197/255, green: 159/255, blue: 103/255))
                                        .cornerRadius(30)
                                }
                                .offset(x: -30, y: -80)
                            }
                        }
                        .frame(maxWidth: .infinity, maxHeight: .infinity)
                        .background(Color("Color_bg"))
                    }
                    .tabItem {
                        VStack{
                            Image(systemName: "house.fill")
                                .resizable()
                                .frame(width: 20, height: 20, alignment: .center)
                            Text("首頁")
                        }
                        
                    }
                    NavigationView{
                        List{
                            NavigationLink(destination: Movie_1View()){
                                VStack{
                                    Text("神秘的魔法石")
                                }
                            }
                            NavigationLink(destination: Movie_2View()){
                                VStack{
                                    Text("消失的密室")
                                }
                            }
                            NavigationLink(destination: Movie_3View()){
                                VStack{
                                    Text("阿茲卡班的逃犯")
                                }
                            }
                            NavigationLink(destination: Movie_4View()){
                                VStack{
                                    Text("火盃的考驗")
                                }
                            }
                            NavigationLink(destination: Movie_5View()){
                                VStack{
                                    Text("鳳凰會的密令")
                                }
                            }
                            NavigationLink(destination: Movie_6View()){
                                VStack{
                                    Text("混血王子的背叛")
                                }
                            }
                            NavigationLink(destination: Movie_7View()){
                                VStack{
                                    Text("死神的聖物(上)")
                                }
                            }
                            NavigationLink(destination: Movie_8View()){
                                VStack{
                                    Text("死神的聖物(下)")
                                }
                            }
                        }
                        .navigationBarTitle(Text("哈利波特系列電影"))
                    }
                    .tabItem {
                        VStack{
                            Image(systemName: "film.fill")
                                .resizable()
                                .frame(width: 20, height: 20, alignment: .center)
                            Text("電影")
                        }
                    }
                }
                .accentColor(Color("under_color_text"))
                .onAppear {
                    let player = AVQueuePlayer()
                    let fileUrl = Bundle.main.url(forResource: "bg_music", withExtension: "mp3")!
                    let item = AVPlayerItem(url: fileUrl)
                    self.looper = AVPlayerLooper(player: player, templateItem: item)
                    player.play()
            }
            }
            
        }
    }
    
}

